mapper.insert:
insert 
into 
	board
	(
	boardnum		
	,board_tag				
	,board_picture
	,board_nickname
	,board_title
	,board_content			
	,to_char(board_inputdate, 'yyyy-mm-dd') board_inputdate		
	,board_hits			
	)
values 
	(
		board_seq.nextval
		,#{board_tag}
		,#{board_picture}
		,#{board_nickname}
		,#{board_title}
		,#{board_content}
		,#{board_inputdate}
		,#{board_hits}		
	)
